import React from "react";
import styled from "styled-components";
import { FaTimes } from "react-icons/fa";
import { Link } from "react-router-dom";

export const NavSidebar = ({ isOpen, toggle }) => {
  return (
    <SNavSidebarContainer isOpen={isOpen} onClick={toggle}>
      <SIcon>
        <SCloseIcon />
      </SIcon>
      <SSidebarWrapper>
        <SSidebarMenu>
          <SSidebarLinks to="/">Home</SSidebarLinks>
          <SSidebarLinks to="/AboutUs">About</SSidebarLinks>
          <SSidebarLinks to="/MenuPrice">Menu & Price</SSidebarLinks>
          <SSidebarLinks to="/Researvation">Researvation</SSidebarLinks>
          <SSidebarExternalLink
            target="blank"
            href="https://meme-clinic.netlify.app/"
          >
            Blog
          </SSidebarExternalLink>
          <SSidebarLinks to="/Contact">Contact</SSidebarLinks>
        </SSidebarMenu>
      </SSidebarWrapper>
    </SNavSidebarContainer>
  );
};

const SNavSidebarContainer = styled.aside`
  position: fixed;
  width: 100%;
  height: 80px;
  background-color: white;
  display: grid;
  align-items: center;
  transition: 0.5s ease-in-out;
  opacity: ${({ isOpen }) => (isOpen ? "100%" : "0")};
  z-index: ${({ isOpen }) => (isOpen ? "1000" : "-100")};
  top: 0;
`;

const SCloseIcon = styled(FaTimes)`
  color: black;
`;

const SIcon = styled.div`
  position: absolute;
  top: 1.2rem;
  right: 1.5rem;
  font-size: 2rem;
  background: transparent;
  outline: none;
  cursor: pointer;
`;

const SSidebarWrapper = styled.div`
  color: white;
`;

const SSidebarMenu = styled.ul`
  display: grid;
  grid-template-columns: 1fr;
  grid-template-rows: repeat(6, 80px);
  text-align: center;

  @media screen and (max-width: 480px) {
    grid-template-rows: repeat(6, 60px);
  }
`;

const SSidebarLinks = styled(Link)`
  background: white;
  white-space: nowrap;
  padding: 30px 60px;
  color: black;
  font-size: 18px;
  font-weight: 700;
  outline: none;
  cursor: pointer;
  text-decoration: none;

  &:hover {
    transition: all 0.1s ease-in-out;
    font-size: 20px;
    border-bottom: 1px solid #01bf71;
  }
`;

const SSidebarExternalLink = styled.a`
  background: white;
  white-space: nowrap;
  padding: 30px 60px;
  color: black;
  font-size: 18px;
  font-weight: bold;
  outline: none;
  cursor: pointer;
  text-decoration: none;

  &:hover {
    transition: all 0.1s ease-in-out;
    font-size: 20px;
    border-bottom: 1px solid #01bf71;
  }
`;
